  display: flex;
  >ul{
    display: flex;
    align-items: end;
    &.header_lnb { 
      font-family: 'Oswald', sans-serif; 
 
    }
    &.header_gnb {
      li {
        a{
          img {
            width: 1.5rem; height: 1.5rem;
          }
        }
      }
    }
  }
}
