            &:nth-child(2){opacity: 0;}
            &:nth-child(3){transform: translateY(-12px) rotate(45deg);}  
          }
        }
        &:nth-child(2){
          display: none;
          position: relative;
          width: 100vw;
          background-color: #fff;
          transition: all 0.2s;
          &.openLnb{
            position: absolute;
            bottom: calc(-167px - 70px - 7px - 5px);
            display: flex;
            flex-direction: column;
            >li {
              width: 100%;
              padding: 5px 0;
              text-align: center;
              border-top: 1px solid #eee;
